<?phprequire_once('includes/required.inc.php');function content($key, $alternative="", $prepend="", $append=""){    if(isset($GLOBALS['CURRENT_PAGE'])){        $page = $GLOBALS['CURRENT_PAGE'];        $contents = $page->getContents();        if(isset($contents[$key])&&$contents[$key]!=null&&$contents[$key]!=""){            return $prepend.$contents[$key].$append;        }else{            return $alternative;        }    }else{        return "";    }}//Check for form results then remove them from the sessionFormResults::setGlobalFromSession();//if(isset($GLOBALS['FORM_RESULTS'])){//    echo "GOT FORM RESULTS";//    echo print_r($GLOBALS['FORM_RESULTS']->getValues());//}if($_GET['uri']=="")$_GET['uri']="index.".$GLOBALS['PAGE_EXTENSION'];if(!preg_match("/^.*\.[^\/]*/", $_GET['uri'])) {    $_GET['uri'] .= ($_GET['uri'][strlen($_GET['uri'])-1]=="/"?"":"/")."index.".$GLOBALS['PAGE_EXTENSION'];}if(Mapping::isURIAnAction($_GET['uri'])) {    $actionMapping = ActionMapping::getActionMapping($_GET['uri']);    $action = $actionMapping->getAction();    require_once($ACTIONS_FOLDER.'/'.$action->getLocation());}else {    $page = Page::getPage($_GET['uri']);    $GLOBALS['CURRENT_PAGE'] = $page;    $contents = $page->getContents();    $template = $page->getTemplate();    $menus = $page->getMenus();    require_once($TEMPLATES_FOLDER.'/'.$template->getLocation());}?>