":"")."\">\n";                            $html .= "\t<span class=\"title\">".$this->title."</span>\n";                            $html .= "\t<span class=\"authors\">";                            for($i=0; $i<count($this->authors); $i++){                                if($i ==  count($this->authors)-1){                                    $html .= " and ";                                }else if($i!=0){                                    $html .= ", ";                                }                                $html .= $this->authors[$i];                            }                            $html .= "</span>\n";                            if($this->workshop!=null){                                $html .= "\t<span class=\"workshop\">".$this->workshop."</span>\n";                            }                            $html .= "\t<span class=\"conference\">".$this->conference."</span>\n";                            $html .= "<a class=\"pdf\" href=\"/uploads/academic/".$this->pdf."\"                                       title=\"".$this->title."\">PDF</a>\n";                            if($showCurrentWork){                                $html .= "<a class=\"source\" href=\"/uploads/academic/".$this->source."\"                                           title=\"".$this->title." - SOURCE\">Source</a>\n";                            }                            $html .= "</li>";                            return $html;                        }                    }                    class Publications{                        private $publications = array();                        public function add($publication){                            if(!isset($this->publications[$publication->year])||!is_array($this->publications[$publication->year])){                                $this->publications[$publication->year] = array();                            }                            $this->publications[$publication->year][] = $publication;                        }                        public function html($showCurrentWork){                            foreach($this->publications as $year=>$pubs){                                $html = "<ul class=\"publications\">\n";                                $html .= "\t<li class=\"year\">\n";                                $html .= "\t<span class=\"year\">".$year."</span>\n";                                foreach($pubs as $p){                                    $html .=  $p->html($showCurrentWork);                                }                                $html .= "\t</li>\n";                                $html .= "</ul>\n";                            }                            return $html;                        }                                            }                    $conferences = new Publications();                    $workshops = new Publications();                    $cosit2009 = new Publication();                    $cosit2009->title= "Assigning Footprints to Dot Sets An Analytical Survey";                    $cosit2009->year = 2009;                    $cosit2009->authors[] = "Antony Galton";                    $cosit2009->conference = "COSIT 2009";                    $cosit2009->pdf = "papers/COSIT2009.pdf";                    $cosit2009->source = "papers/sources/COSIT2009.zip";                    $conferences->add($cosit2009);                    $ecai2010 = new Publication();                    $ecai2010->title= "The Use of Change Identifiers to Update Footprints of Dot Patterns in Real Time";                    $ecai2010->year = 2010;                    $ecai2010->authors[] = "Antony Galton";                    $ecai2010->conference = "ECAI 2010";                    $ecai2010->workshop = "STeDy";                    $ecai2010->pdf = "papers/ECAI2010.pdf";                    $ecai2010->source = "papers/sources/ECAI2010.zip