<div id="header">    <?php function getSection(){        $uri = $GLOBALS['CURRENT_PAGE']->getUri();        if(($lastIndex = strripos($uri, "/"))!==false){            return substr($uri, 0, $lastIndex);        }        return "";    }?>    <a href="<?=$GLOBALS['DOMAIN']?>" title="Home" id="header_link">        <span id="section"><?=getSection()?></span>    </span></a></div>